  mutate(구매여부 = as.integer(구매여부) - 1 ) %>% 
  group_by(AB테스팅) %>% 
  summarise(구매율 = mean(구매여부),
            구매자수 = sum(구매여부),
            전체     = n())

ab_testing_setup %>% 
