five_price_g <- segment_tbl %>% 
  ggplot(aes(x = x, y = y)) +
    expand_limits(x =0, y =0) +
    geom_point(color = "red") +
    geom_rect(aes(xmin = 0, xmax = 50, 
                  ymin = 0, ymax = 20), fill = "gray50") +
